SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_pagamento_indevido ( nr_seq_movto_extrato_p bigint, ie_acao_p text) AS $body$
DECLARE


/* ie_acao_p

M	Marcar
D	Desmarcar

*/
nr_seq_grupo_w	bigint;


BEGIN

if (coalesce(ie_acao_p,'M')	= 'M') then

	select	max(b.nr_seq_grupo)
	into STRICT	nr_seq_grupo_w
	from	extrato_cartao_cr b,
		extrato_cartao_cr_movto a
	where	a.nr_seq_extrato	= b.nr_sequencia
	and	a.nr_sequencia		= nr_seq_movto_extrato_p;

	if (nr_seq_grupo_w IS NOT NULL AND nr_seq_grupo_w::text <> '') then

		CALL desconciliar_movto_redecard(nr_seq_movto_extrato_p);

	end if;

	update	extrato_cartao_cr_movto
	set	ie_pagto_indevido	= 'S'
	where	nr_sequencia		= nr_seq_movto_extrato_p;

else

	if (coalesce(nr_seq_grupo_w::text, '') = '') then

		update	extrato_cartao_cr_movto
		set	ie_pagto_indevido	= 'N',
			vl_liquido		 = NULL
		where	nr_sequencia		= nr_seq_movto_extrato_p;

	else

		update	extrato_cartao_cr_movto
		set	ie_pagto_indevido	= 'N'
		where	nr_sequencia		= nr_seq_movto_extrato_p;

	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
