SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_periodo_prontuarios ( nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
nr_seq_same_w		bigint;
dt_atual_inicial_w	timestamp;
dt_atual_final_w	timestamp;
nr_atendimento_w	bigint;
dt_entrada_w		timestamp;
dt_alta_w		timestamp;
qt_envelopes_w		bigint;
ds_parametro_31_w	varchar(10) := obter_valor_param_usuario(941,31,obter_perfil_ativo,nm_usuario_p,cd_estabelecimento_p);

 
C01 CURSOR FOR 
	SELECT	a.nr_sequencia, 
		a.dt_periodo_inicial, 
		a.dt_periodo_final, 
		b.nr_atendimento, 
		b.dt_entrada, 
		b.dt_alta 
	FROM 	same_prontuario a, 
		atendimento_paciente b 
	WHERE	a.nr_atendimento = b.nr_atendimento 
	AND	a.cd_estabelecimento = cd_estabelecimento_p 
	AND	(a.nr_atendimento IS NOT NULL AND a.nr_atendimento::text <> '') 
	AND	((coalesce(a.dt_periodo_inicial::text, '') = '') OR ((coalesce(a.dt_periodo_final::text, '') = '') AND (b.dt_alta IS NOT NULL AND b.dt_alta::text <> ''))) 
	order by 1;
	

BEGIN 
 
open C01;
loop 
fetch C01 into 
	nr_seq_same_w, 
	dt_atual_inicial_w, 
	dt_atual_final_w, 
	nr_atendimento_w, 
	dt_entrada_w, 
	dt_alta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	 
	select	count(nr_sequencia) 
	into STRICT	qt_envelopes_w 
	from	same_prontuario 
	where	nr_atendimento = nr_atendimento_w;
	 
	if ((qt_envelopes_w = 1) or (qt_envelopes_w > 1 AND ds_parametro_31_w = 'S')) then 
	 
		update	same_prontuario 
		set	dt_periodo_inicial	= TRUNC(coalesce(dt_atual_inicial_w, dt_entrada_w)), 
			dt_periodo_final 	= TRUNC(coalesce(dt_atual_final_w, dt_alta_w)), 
			dt_atualizacao		= clock_timestamp(), 
			nm_usuario		= nm_usuario_p 
		where	nr_sequencia = nr_seq_same_w;
		 
	end if;
		 
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
