CREATE OR REPLACE PROCEDURE atualiza_peso_alt_ciclo ( nr_seq_atendimento_p bigint, nm_usuario_p text, ie_retringir_ciclo_p text) AS $body$
DECLARE

 
nr_ciclo_w		bigint;
ds_dia_ciclo_w		varchar(10);			
qt_altura_w		integer;
qt_peso_w		varchar(10);
nr_seq_paciente_w	bigint;			
			 

BEGIN 
 
select	max(nr_ciclo), 
		max(qt_altura), 
		max(qt_peso), 
		max(nr_seq_paciente), 
		max(ds_dia_ciclo) 
into STRICT	nr_ciclo_w, 
		qt_altura_w, 
		qt_peso_w, 
		nr_seq_paciente_w, 
		ds_dia_ciclo_w 
from	paciente_atendimento 
where	nr_seq_atendimento = nr_seq_atendimento_p;
 
update 	paciente_atendimento 
set 	qt_altura	= qt_altura_w, 
		qt_peso		= qt_peso_w 
where 	nr_seq_paciente = nr_seq_paciente_w 
and		coalesce(nr_prescricao::text, '') = '' 
and		((nr_ciclo	= nr_ciclo_w) or (ie_retringir_ciclo_p = 'N')) 
and		somente_numero(ds_dia_ciclo) >= somente_numero(ds_dia_ciclo_w);
 
commit;
 
if (ie_retringir_ciclo_p = 'N') then 
	nr_ciclo_w := 0;
end if;
 
CALL recalcula_dose_onc_ciclo(nr_seq_paciente_w,nm_usuario_p,nr_ciclo_w);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
