SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_pessoa_orcamento ( nr_cpf_p text, nr_seq_orcamento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
cd_pessoa_fisica_w	pessoa_fisica.cd_pessoa_fisica%type;
nm_pessoa_orcamento_w	orcamento_paciente.nm_pessoa_orcamento%type;
dt_nascimento_w		orcamento_paciente.dt_nascimento%type;
ie_sexo_w		orcamento_paciente.ie_sexo%type;
nr_telefone_w		orcamento_paciente.nr_telefone%type;
ie_cpf_valido_w		varchar(1);


BEGIN 
 
ie_cpf_valido_w := obter_se_cpf_valido(nr_cpf_p);
 
if (ie_cpf_valido_w = 'N') then 
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(271955);
end if;
 
 
select	max(a.cd_pessoa_fisica) 
into STRICT	cd_pessoa_fisica_w 
from	pessoa_fisica a 
where	a.nr_cpf = nr_cpf_p;
 
if (coalesce(cd_pessoa_fisica_w::text, '') = '') then 
	 
	select	nextval('pessoa_fisica_seq') 
	into STRICT	cd_pessoa_fisica_w LIMIT 1;
	 
	select	max(nm_pessoa_orcamento), 
		max(dt_nascimento), 
		max(ie_sexo), 
		max(nr_telefone) 
	into STRICT	nm_pessoa_orcamento_w, 
		dt_nascimento_w, 
		ie_sexo_w, 
		nr_telefone_w 
	from	orcamento_paciente 
	where	nr_sequencia_orcamento = nr_seq_orcamento_p;
	 
	if (coalesce(nm_pessoa_orcamento_w::text, '') = '') or (coalesce(dt_nascimento_w::text, '') = '') or (coalesce(ie_sexo_w::text, '') = '') or (coalesce(nr_telefone_w::text, '') = '') then 
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(272086);
	end if;
	 
	begin 
	insert into pessoa_fisica(cd_pessoa_fisica, 
		ie_tipo_pessoa, 
		nm_pessoa_fisica, 
		dt_atualizacao, 
		nm_usuario, 
		nr_cpf, 
		dt_nascimento, 
		ie_sexo, 
		nr_telefone_celular) 
	values (	cd_pessoa_fisica_w, 
		2, 
		nm_pessoa_orcamento_w, 
		clock_timestamp(), 
		nm_usuario_p, 
		nr_cpf_p, 
		dt_nascimento_w, 
		ie_sexo_w, 
		nr_telefone_w);
	exception 
		when others then 
		cd_pessoa_fisica_w:= null;
	end;
	 
	if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then 
		 
		select 	max(SUBSTR(OBTER_NOME_PF(cd_pessoa_fisica), 0, 60)), 
			max(ie_sexo), 
			max(nr_telefone_celular), 
			max(dt_nascimento) 
		into STRICT	nm_pessoa_orcamento_w, 
			ie_sexo_w, 
			nr_telefone_w, 
			dt_nascimento_w 
		from 	pessoa_fisica 
		where 	cd_pessoa_fisica = cd_pessoa_fisica_w;
		 
		update	orcamento_paciente 
		set	cd_pessoa_fisica = cd_pessoa_fisica_w, 
			nm_pessoa_orcamento = nm_pessoa_orcamento_w, 
			ie_sexo = ie_sexo_w, 
			nr_telefone = nr_telefone_w, 
			dt_nascimento = dt_nascimento_w 
		where	nr_sequencia_orcamento = nr_seq_orcamento_p;
		 
	end if;
	 
 
		 
else 
 
	select 	max(SUBSTR(OBTER_NOME_PF(cd_pessoa_fisica), 0, 60)), 
		max(ie_sexo), 
		max(nr_telefone_celular), 
		max(dt_nascimento) 
	into STRICT	nm_pessoa_orcamento_w, 
		ie_sexo_w, 
		nr_telefone_w, 
		dt_nascimento_w 
	from 	pessoa_fisica 
	where 	cd_pessoa_fisica = cd_pessoa_fisica_w;
	 
	update	orcamento_paciente 
	set	cd_pessoa_fisica = cd_pessoa_fisica_w, 
		nm_pessoa_orcamento = nm_pessoa_orcamento_w, 
		ie_sexo = ie_sexo_w, 
		nr_telefone = nr_telefone_w, 
		dt_nascimento = dt_nascimento_w 
	where	nr_sequencia_orcamento = nr_seq_orcamento_p;
	 
end if;
	 
 
commit;
 
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
