CREATE OR REPLACE PROCEDURE atualiza_preco_simpro ( cd_tuss_p bigint, nm_usuario_p text, ie_classificacao_p text, cd_simpro_p bigint, dt_vigencia_p timestamp, dt_fora_linha_item_p text, dt_validade_p text ) AS $body$
DECLARE


cd_estab_simpro_w	estabelecimento.cd_estabelecimento%type;			
dt_fora_linha_item_w	simpro_preco.dt_fora_linha_item%type;


BEGIN

if (dt_fora_linha_item_p = 'null') then
	dt_fora_linha_item_w	:= null;
elsif (dt_fora_linha_item_p = 'sysdate') then
	dt_fora_linha_item_w	:= clock_timestamp();
elsif (dt_fora_linha_item_p IS NOT NULL AND dt_fora_linha_item_p::text <> '') then
	dt_fora_linha_item_w	:= to_date(dt_fora_linha_item_p, 'dd/mm/yyyy');
end if;

if (cd_simpro_p IS NOT NULL AND cd_simpro_p::text <> '') then
	begin
	
	cd_estab_simpro_w := wheb_usuario_pck.get_cd_estabelecimento;
	
	update 		simpro_preco
	set 		cd_tuss = cd_tuss_p,
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p,
			ie_classificacao = ie_classificacao_p,
			dt_fora_linha_item = dt_fora_linha_item_w ,
			dt_validade = coalesce(dt_validade_p, dt_validade)
	where		cd_simpro = cd_simpro_p
	and		dt_vigencia = dt_vigencia_p
	and     	coalesce(cd_estabelecimento, coalesce(cd_estab_simpro_w, 0))	= coalesce(cd_estab_simpro_w, 0);
						
	end;
end if;

end;
$body$
