CREATE OR REPLACE PROCEDURE atualiza_prescricao_motion (nr_prescricao_p bigint, nr_sequencia_p bigint, nr_acesso_dicom_p text, nr_seq_interno_p bigint, ie_status_p text, ds_erro_p INOUT text) AS $body$
