CREATE OR REPLACE PROCEDURE atualiza_prescricao_orcamento ( nr_sequencia_orcamento_p bigint, nr_prescricao_p bigint, ie_acao_p text, ie_excluir_mat_proc_p text, nm_usuario_p text) AS $body$
DECLARE


ie_possui_registros_w   varchar(2);


BEGIN
if (ie_acao_p = 'E') then
   select coalesce(max('S'), 'N')
   into STRICT ie_possui_registros_w
   from prescr_opm
   where nr_prescricao = nr_prescricao_p;

   if (ie_possui_registros_w = 'N') then
      update orcamento_paciente
      set       nr_prescricao     = NULL,
                  dt_atualizacao  = clock_timestamp(),
                  nm_usuario       = nm_usuario_p
      where  nr_prescricao   = nr_prescricao_p;
   end if;

   if (coalesce(ie_excluir_mat_proc_p, 'N') = 'S') then
      delete from prescr_material where nr_prescricao = nr_prescricao_p;
      delete from prescr_procedimento where nr_prescricao = nr_prescricao_p;
   end if;
else
   update orcamento_paciente
   set       nr_prescricao    = nr_prescricao_p,
               dt_atualizacao  = clock_timestamp(),
               nm_usuario       = nm_usuario_p
   where  nr_sequencia_orcamento = nr_sequencia_orcamento_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
