CREATE OR REPLACE PROCEDURE atualiza_prescr_agendamento (nr_seq_agenda_p bigint, nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
ie_gerar_passagem_w	varchar(1);
ie_lote_sem_atendimento_w	varchar(1);
cd_setor_atendimento_w	integer;
cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;
				

BEGIN 
cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;
 
ie_gerar_passagem_w := Obter_param_Usuario(916, 1079, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ie_gerar_passagem_w);
 
update	prescr_medica 
set	nr_atendimento 	= nr_atendimento_p, 
	dt_atualizacao 	= clock_timestamp(), 
	nm_usuario	= nm_usuario_p 
where	nr_seq_agenda 	= nr_seq_agenda_p 
and	coalesce(nr_atendimento::text, '') = '';
 
 
if (ie_gerar_passagem_w = 'S') then 
 
	select	max(cd_setor_atendimento) 
	into STRICT	cd_setor_atendimento_w 
	from	prescr_medica 
	where 	nr_seq_agenda 	= nr_seq_agenda_p;
	 
	if (coalesce(cd_setor_atendimento_w,0) > 0) then 
	 
		CALL gerar_passagem_setor_atend(nr_atendimento_p,cd_setor_atendimento_w,clock_timestamp(),'S',nm_usuario_p);
	 
	end if;
end if;
 
ie_lote_sem_atendimento_w := Obter_param_Usuario(7029, 110, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ie_lote_sem_atendimento_w);
if (ie_lote_sem_atendimento_w = 'S') then 
	CALL baixar_ap_lote_consistido(nr_atendimento_p,cd_estabelecimento_w,nm_usuario_p);
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
