SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_prescr_mat_motivo ( nr_prescricao_p bigint, nr_sequencia_p bigint, nm_usuario_p text) AS $body$
BEGIN
	if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
		begin
			UPDATE	prescr_material
			SET	dt_baixa	= clock_timestamp(),
				cd_motivo_baixa = 1
			WHERE	nr_prescricao 	= nr_prescricao_p
			AND	nr_sequencia 	= nr_sequencia_p
			AND	cd_motivo_baixa = 0;
		end;
	end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
