CREATE OR REPLACE PROCEDURE atualiza_prescr_proced_qt_lado (ie_lado_p text, nr_prescricao_p bigint, nr_sequencia_p bigint, cd_perfil_p bigint, nm_usuario_p text, cd_estabelecimento_p text) AS $body$
DECLARE


qt_proced_param_w			prescr_procedimento.qt_procedimento%type;
qt_procedimento_w				prescr_procedimento.qt_procedimento%type;
qt_procedimento_prescr_w		prescr_procedimento.qt_procedimento%type;


BEGIN

qt_proced_param_w := Obter_Param_Usuario(924, 147, cd_perfil_p, nm_usuario_p, cd_estabelecimento_p, qt_proced_param_w);

select 	max(qt_procedimento)
into STRICT 	qt_procedimento_prescr_w
from	prescr_procedimento
where	nr_prescricao = nr_prescricao_p
and 	nr_sequencia = nr_sequencia_p;

qt_procedimento_w := qt_procedimento_prescr_w;
if (coalesce(qt_proced_param_w,0) > coalesce(qt_procedimento_prescr_w,0)) and (ie_lado_p = 'A') then
	qt_procedimento_w := qt_proced_param_w;
end if;

if (ie_lado_p IS NOT NULL AND ie_lado_p::text <> '')	then
	update	prescr_procedimento
	set		ie_lado			= ie_lado_p,
			qt_procedimento = qt_procedimento_w
	where	nr_prescricao	= nr_prescricao_p
	and		nr_sequencia	= nr_sequencia_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
