SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_prestador_conpaci ( nr_interno_conta_p bigint, nm_usuario_p text) AS $body$
DECLARE

			 
			 
cd_estabelecimento_w   		smallint 	:= 0;
cd_convenio_w			integer;
cd_setor_atendimento_w		integer;
cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
ie_tipo_atendimento_w		smallint;
nr_seq_exame_w			bigint;
nr_seq_proc_interno_w		bigint;
cd_medico_executor_w		varchar(10);
ie_medico_executor_w		varchar(1);
cd_cgc_prestador_w		varchar(14);
nr_sequencia_w			bigint;
dt_procedimento_w			timestamp;

nr_atendimento_w		bigint;
nr_seq_classificacao_w		bigint;
nr_prescricao_w			bigint;
nr_sequencia_prescricao_w	integer;
ie_origem_inf_w			varchar(1);
cd_cgc_laboratorio_w		varchar(14);
cd_setor_prescricao_w		integer;			
			 
			 
c01 CURSOR FOR 
	SELECT	a.cd_estabelecimento, 
		a.cd_convenio_parametro, 
		b.cd_setor_atendimento, 
		b.cd_procedimento, 
		b.ie_origem_proced, 
		obter_tipo_atendimento(a.nr_atendimento), 
		b.nr_seq_exame, 
		b.nr_seq_proc_interno, 
		b.cd_medico_executor, 
		b.nr_sequencia, 
		b.dt_procedimento, 
		a.nr_atendimento, 
		b.nr_prescricao, 
		b.nr_sequencia_prescricao 
	from	procedimento_paciente b, 
		conta_paciente a 
	where	a.nr_interno_conta	= b.nr_interno_conta 
	and	a.nr_interno_conta	= nr_interno_conta_p 
	and	coalesce(b.cd_motivo_exc_conta::text, '') = '';
	
 
 

BEGIN 
open c01;
loop 
fetch c01 into	 
	cd_estabelecimento_w, 
	cd_convenio_w, 
	cd_setor_atendimento_w, 
	cd_procedimento_w, 
	ie_origem_proced_w, 
	ie_tipo_atendimento_w, 
	nr_seq_exame_w, 
	nr_seq_proc_interno_w, 
	cd_medico_executor_w, 
	nr_sequencia_w, 
	dt_procedimento_w, 
	nr_atendimento_w, 
	nr_prescricao_w, 
	nr_sequencia_prescricao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	 
	nr_seq_classificacao_w := obter_classif_atendimento(nr_atendimento_w);
	 
	if (coalesce(nr_prescricao_w,0) > 0) then 
		 
		select	max(cd_setor_atendimento) 
		into STRICT	cd_setor_prescricao_w 
		from	prescr_medica 
		where	nr_prescricao = nr_prescricao_w;
	 
		if (coalesce(nr_sequencia_prescricao_w,0) > 0) then 
			select	max(ie_origem_inf), 
				max(cd_cgc_laboratorio) 
			into STRICT	ie_origem_inf_w, 
				cd_cgc_laboratorio_w 
			from	prescr_procedimento 
			where	nr_prescricao = nr_prescricao_w 
			and	nr_sequencia = nr_sequencia_prescricao_w;
		end if;
		 
	end if;
	 
	SELECT * FROM consiste_medico_executor(	cd_estabelecimento_w, cd_convenio_w, cd_setor_atendimento_w, cd_procedimento_w, ie_origem_proced_w, ie_tipo_atendimento_w, nr_seq_exame_w, nr_seq_proc_interno_w, ie_medico_executor_w, cd_cgc_prestador_w, cd_medico_executor_w, cd_medico_executor_w, cd_medico_executor_w, dt_procedimento_w, nr_seq_classificacao_w, ie_origem_inf_w, cd_cgc_laboratorio_w, cd_setor_prescricao_w) INTO STRICT ie_medico_executor_w, cd_cgc_prestador_w, cd_medico_executor_w, cd_medico_executor_w;
					 
					 
	update	procedimento_paciente 
	set	cd_cgc_prestador	= coalesce(cd_cgc_prestador_w,cd_cgc_prestador) 
	where	nr_sequencia		= nr_sequencia_w;
	end;
end loop;
close c01;
 
CALL recalcular_conta_paciente(nr_interno_conta_p,nm_usuario_p);
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE atualiza_prestador_conpaci ( nr_interno_conta_p bigint, nm_usuario_p text) FROM PUBLIC;

