SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_prestador_matpaci ( nr_interno_conta_p bigint, nm_usuario_p text) AS $body$
DECLARE

			 
			 
cd_estabelecimento_w  	smallint 	:= 0;
cd_convenio_w			integer;
cd_categoria_w			varchar(10);
ie_tipo_atendimento_w		smallint;
cd_setor_atendimento_w		integer;
cd_material_w			integer;
nr_sequencia_w			bigint;
cd_grupo_material_w		smallint;
cd_subgrupo_material_w		smallint;
cd_classe_material_w		integer;
cd_cgc_prestador_w		varchar(14);
cd_medico_resp_w		varchar(10);
dt_atendimento_w		timestamp;
cd_cgc_fornecedor_w		material_atend_paciente.cd_cgc_fornecedor%type;
			
			 
c01 CURSOR FOR 
	SELECT	a.cd_estabelecimento, 
		a.cd_convenio_parametro, 
		a.cd_categoria_parametro, 
		obter_tipo_atendimento(a.nr_atendimento), 
		b.cd_setor_atendimento, 
		b.cd_material, 
		b.nr_sequencia, 
		b.dt_atendimento, 
		b.cd_cgc_fornecedor 
	from	material_atend_paciente b, 
		conta_paciente a 
	where	a.nr_interno_conta	= b.nr_interno_conta 
	and	a.nr_interno_conta	= nr_interno_conta_p 
	and	coalesce(b.cd_motivo_exc_conta::text, '') = '';
	
 
 

BEGIN 
 
select	max(a.cd_medico_resp) 
into STRICT	cd_medico_resp_w 
from 	conta_paciente b, 
	atendimento_paciente a 
where	a.nr_atendimento	= b.nr_atendimento 
and	b.nr_interno_conta 	= nr_interno_conta_p;
 
open c01;
loop 
fetch c01 into	 
	cd_estabelecimento_w, 
	cd_convenio_w, 
	cd_categoria_w, 
	ie_tipo_atendimento_w, 
	cd_setor_atendimento_w, 
	cd_material_w,	 
	nr_sequencia_w, 
	dt_atendimento_w, 
	cd_cgc_fornecedor_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	 
	select	cd_grupo_material, 
		cd_subgrupo_material, 
		cd_classe_material		 
	into STRICT	cd_grupo_material_w, 
		cd_subgrupo_material_w, 
		cd_classe_material_w 
	from	estrutura_material_v 
	where	cd_material = cd_material_w;
	 
	select	substr(obter_prestador_material(cd_estabelecimento_w, cd_convenio_w, cd_categoria_w, ie_tipo_atendimento_w, 
				cd_setor_atendimento_w, cd_grupo_material_w, cd_subgrupo_material_w, cd_classe_material_w, 
				cd_material_w, cd_medico_resp_w, dt_atendimento_w, cd_cgc_fornecedor_w),1,14) 
	into STRICT	cd_cgc_prestador_w 
	;
					 
	update	material_atend_paciente 
	set	cd_cgc_prestador	= coalesce(cd_cgc_prestador_w,cd_cgc_prestador) 
	where	nr_sequencia		= nr_sequencia_w;
	end;
end loop;
close c01;
 
CALL recalcular_conta_paciente(nr_interno_conta_p,nm_usuario_p);
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
