CREATE OR REPLACE PROCEDURE atualiza_procedencia_atend ( nr_atendimento_p bigint) AS $body$
DECLARE

					 
cd_setor_atendimento_w	bigint;
ie_clinica_w		varchar(10);
cd_convenio_w		bigint;

nr_seq_regra_w		bigint;
cd_procedencia_w	integer;
qt_dias_atend_w		bigint;
qt_dias_atend_regra_w	bigint;

-- 
cd_pessoa_fisica_w		varchar(20);
nr_atendimento_ult_w		bigint;
cd_setor_atendimento_atual_w	bigint;


BEGIN 
 
 
 
 
select	cd_pessoa_fisica 
into STRICT	cd_pessoa_fisica_w 
from	atendimento_paciente 
where	nr_atendimento	=	nr_atendimento_p;
 
select	max(nr_atendimento) 
into STRICT	nr_atendimento_ult_w 
from	atendimento_paciente 
where	cd_pessoa_fisica = cd_pessoa_fisica_w 
and	nr_atendimento <> nr_atendimento_p;
 
select	coalesce(max(obter_setor_atendimento(nr_atendimento)),0), 
	coalesce(max(ie_clinica),0), 
	coalesce(max(obter_convenio_atendimento(nr_atendimento)),0), 
	coalesce(max(trunc(clock_timestamp() - dt_entrada)),0) 
into STRICT	cd_setor_atendimento_w, 
	ie_clinica_w, 
	cd_convenio_w, 
	qt_dias_atend_w 
from	atendimento_paciente 
where	nr_atendimento = nr_atendimento_ult_w;
 
select	coalesce(max(obter_setor_atendimento(nr_atendimento)),0) 
into STRICT	cd_setor_atendimento_atual_w 
from	atendimento_paciente 
where	nr_atendimento = nr_atendimento_p;
 
 
begin 
	select	nr_sequencia 
	into STRICT	nr_seq_regra_w 
	from	regra_procedencia_atend 
	where	coalesce(cd_setor_atendimento, coalesce(cd_setor_atendimento_w, 0)) = coalesce(cd_setor_atendimento_w, 0) 
	and	coalesce(ie_clinica, coalesce(ie_clinica_w, 0)) = coalesce(ie_clinica_w, 0) 
	and	coalesce(cd_convenio, coalesce(cd_convenio_w, 0)) = coalesce(cd_convenio_w, 0) 
	and	coalesce(qt_dias_atendimento, 9999) >= coalesce(qt_dias_atend_w,0) 
	and	coalesce(cd_setor_atend_atual, coalesce(cd_setor_atendimento_atual_w, 0)) = coalesce(cd_setor_atendimento_atual_w, 0)		 
	and	(cd_procedencia IS NOT NULL AND cd_procedencia::text <> '')  LIMIT 1;
exception 
	when 	others then 
		nr_seq_regra_w := 0;
end;
 
if (nr_seq_regra_w > 0) then 
	 
	select	coalesce(max(cd_procedencia), 0) 
	into STRICT	cd_procedencia_w 
	from	regra_procedencia_atend 
	where	nr_sequencia = nr_seq_regra_w;
	 
	if (cd_procedencia_w > 0) then 
		update	atendimento_paciente 
		set	cd_procedencia = cd_procedencia_w 
		where	nr_atendimento = nr_atendimento_p;
	end if;
		 
end if;
	 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
