SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_proc_atend_conv ( nr_atendimento_p bigint, cd_convenio_p bigint default null, cd_categoria_p bigint default null) AS $body$
DECLARE



cd_convenio_w		bigint;
cd_categoria_w		bigint;
nr_prescricao_w 	bigint;
nr_seq_proc_interno_w	bigint;
cd_procedimento_w	bigint;
ie_origem_proced_w	integer;
cd_estabelecimento_w	bigint;	
cd_plano_convenio_w	varchar(10);
cd_tipo_acomodacao_w	smallint;
ie_tipo_atendimento_w   atendimento_paciente.ie_tipo_atendimento%type;


c01 CURSOR FOR	
	SELECT	b.nr_seq_proc_interno,
		a.nr_prescricao
	from	prescr_medica a,
		prescr_procedimento b
	where	a.nr_atendimento = nr_atendimento_p
	and	b.nr_prescricao = a.nr_prescricao
	and	(b.nr_seq_proc_interno IS NOT NULL AND b.nr_seq_proc_interno::text <> '')
	and	coalesce(a.dt_liberacao::text, '') = '';


BEGIN

select	coalesce(cd_convenio_p,max(cd_convenio)),
	coalesce(cd_categoria_p,max(cd_categoria)),
	max(cd_plano_convenio),
	max(cd_tipo_acomodacao)
into STRICT	cd_convenio_w,
	cd_categoria_w,
	cd_plano_convenio_w,
	cd_tipo_acomodacao_w
from	atend_categoria_convenio
where	nr_seq_interno = obter_atecaco_atendimento(nr_atendimento_p);

select	max(cd_estabelecimento), max(ie_tipo_atendimento)
into STRICT	cd_estabelecimento_w, ie_tipo_atendimento_w
from	atendimento_paciente
where	nr_atendimento = nr_atendimento_p;

open c01;
loop
fetch 	c01 into
	nr_seq_proc_interno_w,
	nr_prescricao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	
	begin
	
		SELECT * FROM Obter_Proc_Tab_Interno_Conv2(	nr_seq_proc_interno_w, cd_estabelecimento_w, cd_convenio_w, cd_categoria_w, cd_plano_convenio_w, clock_timestamp(), cd_tipo_acomodacao_w, cd_procedimento_w, ie_origem_proced_w, ie_tipo_atendimento_w) INTO STRICT cd_procedimento_w, ie_origem_proced_w;
	
	
	exception
		when 	others then
			cd_procedimento_w := 0;
			ie_origem_proced_w := 0;
	end;
		
	if (cd_procedimento_w > 0)
	and (ie_origem_proced_w > 0) then
		update	prescr_procedimento
		set	cd_procedimento = cd_procedimento_w,
			ie_origem_proced = ie_origem_proced_w
		where	nr_prescricao = nr_prescricao_w
		and	nr_seq_proc_interno = nr_seq_proc_interno_w;
	end if;
	
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
