SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_proc_niss_null_cirur ( nr_seq_proc_p bigint, nr_seq_proced_niss_p bigint) AS $body$
DECLARE


nr_cirurgia_w			bigint;
qt_update_w				bigint;
nr_seq_proced_niss_w	bigint;
nr_seq_proc_interno_w	bigint;

c01 CURSOR FOR
	SELECT 	c.nr_cirurgia
	from	cirurgia c
	where 	c.nr_seq_proc_interno	= 	nr_seq_proc_p
	and	coalesce(c.nr_seq_proced_niss::text, '') = '';


BEGIN

qt_update_w := 0;

open c01;
	loop
	fetch c01 into
		nr_cirurgia_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		update 	cirurgia
		set	nr_seq_proced_niss 	= 	nr_seq_proced_niss_p
		where	nr_cirurgia 		= 	nr_cirurgia_w;

		qt_update_w := qt_update_w + 1;

		if (qt_update_w >= 5000) then

			qt_update_w := 0;
			commit;

		end if;

		end;
	end loop;
	close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
