CREATE OR REPLACE PROCEDURE atualiza_profiss_perda_ganho ( nr_sequencia_p bigint, cd_pessoa_fisica_p text) AS $body$
DECLARE


cd_professional_w	varchar(10);


BEGIN

if (nr_sequencia_p > 0) then

	select	max(cd_profissional)
	into STRICT	cd_professional_w
	from	atendimento_perda_ganho
	where	nr_sequencia	= nr_sequencia_p;

	if (coalesce(cd_professional_w::text, '') = '' or cd_professional_w = '') then
		update	atendimento_perda_ganho
		set	cd_profissional	=	cd_pessoa_fisica_p
		where	nr_sequencia	=	nr_sequencia_p;
	end if;

	commit;

end if;

end;
