SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_qtd_emprestimo ( nr_emprestimo_p bigint, nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_devolucao_w		double precision;
qt_itens_w		double precision;
qt_saldo_w		double precision;
ie_situacao_w		varchar(1);


BEGIN

select	coalesce(sum(qt_material),0)
into STRICT	qt_devolucao_w
from	emprestimo_material_dev
where	nr_emprestimo	= nr_emprestimo_p
and	nr_sequencia	= nr_sequencia_p;

update	emprestimo_material
set	qt_material = qt_emprestimo - qt_devolucao_w
where	nr_emprestimo	= nr_emprestimo_p
and	nr_sequencia	= nr_sequencia_p;

select	ie_situacao
into STRICT	ie_situacao_w
from	emprestimo
where	nr_emprestimo = nr_emprestimo_p;

select	count(*),
	sum(qt_material)
into STRICT	qt_itens_w,
	qt_saldo_w
from	emprestimo_material
where	nr_emprestimo = nr_emprestimo_p;

if (qt_itens_w > 0) then
	ie_situacao_w := 'A';
	if (qt_saldo_w = 0) then
		ie_situacao_w := 'B';
	end if;

	update 	emprestimo
	set	ie_situacao = ie_situacao_w
	where	nr_emprestimo = nr_emprestimo_p;
elsif (ie_situacao_w = 'B') then

	update	emprestimo
	set	ie_situacao = 'A'
	where	nr_emprestimo = nr_emprestimo_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
