CREATE OR REPLACE PROCEDURE atualiza_qtd_taxa_equipamento ( cd_estabelecimento_p bigint, nr_seq_equipamento_p bigint, nm_usuario_p text ) AS $body$
DECLARE


qt_equipamento_w	bigint;
ds_lista_equip_w	varchar(2000);
ie_atualiza_equip_w	varchar(1):= 'S';


BEGIN

ds_lista_equip_w := Obter_Param_Usuario(872, 331, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ds_lista_equip_w);

if (ds_lista_equip_w IS NOT NULL AND ds_lista_equip_w::text <> '') then
	select 	coalesce(max('S'),'N')
	into STRICT	ie_atualiza_equip_w
	from 	equipamento_cirurgia a
	where 	a.nr_sequencia = nr_seq_equipamento_p
	and	obter_se_contido(a.cd_equipamento,ds_lista_equip_w) = 'N'
	and	coalesce(a.ie_situacao,'A') = 'A';
end if;

if (ie_atualiza_equip_w = 'S') then
	select 	max(a.qt_equipamento)
	into STRICT	qt_equipamento_w
	from 	equipamento_cirurgia a
	where 	a.nr_sequencia = nr_seq_equipamento_p
	and	coalesce(a.ie_situacao,'A') = 'A';

	update	taxa_equipamento_cirurgia
	set	qt_taxa		= qt_equipamento_w
	where	nr_seq_equi_cir = nr_seq_equipamento_p;

	update	material_equip_cirurgia
	set	qt_material 	= qt_equipamento_w
	where	nr_seq_equi_cir = nr_seq_equipamento_p;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
