CREATE OR REPLACE PROCEDURE atualiza_recebimento_medic ( nr_seq_atendimento_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


/* ie_opcao_p
	G - gerar
	D - desfazer */
BEGIN

if (ie_opcao_p = 'G') then
	begin
	update	paciente_atendimento
	set	dt_recebimento_medic	= clock_timestamp(),
		nm_usuario		= nm_usuario_p,
		nm_usuario_receb		= nm_usuario_p
	where	nr_seq_atendimento	= nr_seq_atendimento_p;
	end;
elsif (ie_opcao_p = 'D') then
	begin
	update	paciente_atendimento
	set	dt_recebimento_medic	 = NULL,
		nm_usuario	= nm_usuario_p,
		nm_usuario_receb	 = NULL
	where	nr_seq_atendimento	= nr_seq_atendimento_p;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
