CREATE OR REPLACE PROCEDURE atualiza_receb_item_mat_req ( nm_usuario_receb_p text, nr_requisicao_p bigint, nm_usuario_p text) AS $body$
DECLARE


nm_usuario_recebedor_w		varchar(15);

BEGIN

if (nm_usuario_receb_p IS NOT NULL AND nm_usuario_receb_p::text <> '') then
	begin
	select	max(nm_usuario)
	into STRICT	nm_usuario_recebedor_w
	from	usuario
	where (upper(nm_usuario)	= upper(nm_usuario_receb_p)
	or	upper(ds_login)	= upper(nm_usuario_receb_p));
	end;
end if;


if (nm_usuario_recebedor_w IS NOT NULL AND nm_usuario_recebedor_w::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (nr_requisicao_p IS NOT NULL AND nr_requisicao_p::text <> '') then
	begin
	update	requisicao_material
	set	nm_usuario_recebedor = nm_usuario_recebedor_w,
		dt_recebimento = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	nr_requisicao = nr_requisicao_p;
	end;
end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
