CREATE OR REPLACE PROCEDURE atualiza_regra_baixa_exame ( nr_atendimento_p bigint, nr_prescricao_p bigint, nr_seq_procedimento_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_setor_atendimento_w		bigint;
cd_area_proced_w		bigint;
cd_espec_proced_w		bigint;
cd_grupo_proced_w		bigint;
cd_motivo_baixa_w		bigint;
qt_procedimento_w		double precision;
qt_proc_prescr_w		double precision;
ie_considera_atend_w		varchar(1);

c01 CURSOR FOR
SELECT	cd_motivo_baixa,
	qt_procedimento,
	coalesce(ie_considera_atendimento,'S')
from	regra_motivo_baixa_exame
where	cd_setor_atendimento	= cd_setor_atendimento_w
and	((cd_area_procedimento	= cd_area_proced_w) or (coalesce(cd_area_procedimento::text, '') = ''))
and	((cd_especialidade	= cd_espec_proced_w) or (coalesce(cd_especialidade::text, '') = ''))
and	((cd_grupo_proc		= cd_grupo_proced_w) or (coalesce(cd_grupo_proc::text, '') = ''))
and	((cd_procedimento	= cd_procedimento_p) or (coalesce(cd_procedimento::text, '') = ''))
and	((coalesce(cd_procedimento::text, '') = '') or ((ie_origem_proced = ie_origem_proced_p) or (coalesce(ie_origem_proced::text, '') = '')));


BEGIN

select	max(cd_setor_atendimento)
into STRICT	cd_setor_atendimento_w
from	prescr_medica
where	nr_prescricao	= nr_prescricao_p;

select	coalesce(max(cd_area_procedimento),0),
	coalesce(max(cd_especialidade),0),
	coalesce(max(cd_grupo_proc),0)
into STRICT	cd_area_proced_w,
	cd_espec_proced_w,
	cd_grupo_proced_w
from	estrutura_procedimento_v
where	cd_procedimento		= cd_procedimento_p
and	ie_origem_proced	= ie_origem_proced_p;

open C01;
loop
fetch C01 into
	cd_motivo_baixa_w,
	qt_procedimento_w,
	ie_considera_atend_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	select	sum(a.qt_procedimento)
	into STRICT	qt_proc_prescr_w
	from	prescr_medica b,
		prescr_procedimento a
	where	a.nr_prescricao		= b.nr_prescricao
	and	((b.nr_atendimento	= nr_atendimento_p) or
		(ie_considera_atend_w = 'N' AND b.cd_setor_atendimento = cd_setor_atendimento_w))
	and	coalesce(b.dt_suspensao::text, '') = ''
	and	coalesce(a.dt_suspensao::text, '') = ''
	and	a.cd_procedimento 	= cd_procedimento_p
	and	a.ie_origem_proced 	= ie_origem_proced_p;

	if (coalesce(cd_motivo_baixa_w,0) <> 0) and (qt_procedimento_w	>= 0) and (qt_procedimento_w	<= qt_proc_prescr_w) then
		update	prescr_procedimento
		set	cd_motivo_baixa	= cd_motivo_baixa_w,
			dt_baixa	= clock_timestamp()
		where	nr_prescricao	= nr_prescricao_p
		and	nr_sequencia	= nr_seq_procedimento_p;
	end if;

end loop;
close C01;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
