SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_regra_copia_crit ( nr_sequencia_p bigint) AS $body$
DECLARE


qt_regra_copia_hor_w	smallint;
ie_regra_geral_w		varchar(2);

BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	begin
	select 	count(nr_sequencia)
	into STRICT 	qt_regra_copia_hor_w
	from   	rep_regra_copia_hor
	where  	nr_seq_regra_copia = nr_sequencia_p;

	if (qt_regra_copia_hor_w > 0) then
		begin
		select	max(ie_regra_geral)
		into STRICT	ie_regra_geral_w
		from	rep_regra_copia_crit
		where	nr_sequencia = nr_sequencia_p;

		if (ie_regra_geral_w	= '1H') then
			begin
			update	rep_regra_copia_hor
			set     ie_regra_horarios  = NULL
			where   nr_seq_regra_copia = nr_sequencia_p;
			end;
		elsif (ie_regra_geral_w = 'H') then
			begin
			update	rep_regra_copia_hor
			set     ie_regra_prim_hor_proc  = NULL
			where	nr_seq_regra_copia = nr_sequencia_p;
			end;
		elsif (ie_regra_geral_w = 'I') then
			begin
			delete  FROM rep_regra_copia_hor
			where   nr_seq_regra_copia = nr_sequencia_p;
			end;
		end if;
		end;
	end if;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
