CREATE OR REPLACE PROCEDURE atualiza_req_agenda ( nr_prescricao_p bigint, nm_usuario_p text, cd_medico_p text) AS $body$
DECLARE

nr_seq_agenda_w		prescr_medica.nr_seq_agenda%type;


BEGIN
if (cd_medico_p > '0') then
	begin
	select	coalesce(max(nr_seq_agenda),0)
	into STRICT	nr_seq_agenda_w
	from	prescr_medica
	where	nr_prescricao	= nr_prescricao_p;
	
	if (nr_seq_agenda_w > 0) then
		begin
		update	agenda_paciente
		set	cd_medico	= cd_medico_p
		where	nr_sequencia	= nr_seq_agenda_w;	
		end;
	end if;
	
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
