CREATE OR REPLACE PROCEDURE atualiza_resultado_visita (nr_seq_atendimento_p mprev_atendimento.nr_sequencia%type, ie_resultado_visita_p text, ds_observacao_p text) AS $body$
DECLARE

													
nr_seq_partic_ciclo_item_w bigint;


BEGIN

select  max(c.nr_sequencia) as sequencia
into STRICT		nr_seq_partic_ciclo_item_w
from	mprev_atend_paciente a,
        	mprev_participante b,
        	mprev_partic_ciclo_item_v c
where	  a.nr_seq_participante = b.nr_sequencia
and     b.cd_pessoa_fisica = c.cd_pessoa_fisica
and   	a.nr_seq_atendimento = nr_seq_atendimento_p
and     C.IE_FORMA_ATEND_PREV = 'D'
and     Wheb_assist_pck.get_nivel_atencao_perfil in ('P','S');

update	mprev_partic_ciclo_item
set			ie_resultado_visita =  ie_resultado_visita_p,
			DS_OBSERVACAO =  ds_observacao_p
where			nr_sequencia = nr_seq_partic_ciclo_item_w;
commit;
end;
