CREATE OR REPLACE PROCEDURE atualiza_result_evidencia ( nr_seq_diag_p pe_prescr_item_result_diag.nr_seq_diag%TYPE, nr_seq_item_p pe_prescr_item_result.nr_seq_item%TYPE, nr_seq_prescr_p pe_prescr_item_result.nr_seq_prescr%TYPE, nr_seq_result_p pe_prescr_item_result.nr_seq_result%TYPE, nm_usuario_p text, ie_acao_p text ) AS $body$
DECLARE

    NR_SEQ_ITEM_RESULT_W bigint := null;
    NR_SEQ_ITEM_DIAG_W bigint := null;
    NR_COUNT_W bigint := null;

BEGIN

    SELECT MAX(NR_SEQUENCIA) INTO STRICT NR_SEQ_ITEM_RESULT_W FROM PE_PRESCR_ITEM_RESULT a WHERE
            a.NR_SEQ_ITEM = nr_seq_item_p AND
            a.NR_SEQ_PRESCR = nr_seq_prescr_p AND
            a.NR_SEQ_RESULT = nr_seq_result_p;

    IF (ie_acao_p = 'I') THEN

        if (coalesce(NR_SEQ_ITEM_RESULT_W::text, '') = '') THEN
        
            SELECT nextval('pe_prescr_item_result_seq') INTO STRICT NR_SEQ_ITEM_RESULT_W;
            INSERT INTO PE_PRESCR_ITEM_RESULT(NR_SEQUENCIA, DT_ATUALIZACAO,NM_USUARIO, NR_SEQ_ITEM, NR_SEQ_RESULT, NR_SEQ_PRESCR)
                    values (NR_SEQ_ITEM_RESULT_W, clock_timestamp(), nm_usuario_p, nr_seq_item_p, nr_seq_result_p, nr_seq_prescr_p);
            COMMIT;
        ELSE
        
            UPDATE PE_PRESCR_ITEM_RESULT SET DT_ATUALIZACAO_NREC = clock_timestamp(), NM_USUARIO_NREC = nm_usuario_p
                    WHERE NR_SEQUENCIA = NR_SEQ_ITEM_RESULT_W;
        END IF;

            SELECT nextval('pe_prescr_item_result_diag_seq') INTO STRICT NR_SEQ_ITEM_DIAG_W;
            INSERT INTO pe_prescr_item_result_diag a(NR_SEQUENCIA, NR_SEQ_DIAG,NR_SEQ_ITEM, DT_ATUALIZACAO, NM_USUARIO)
                    VALUES (NR_SEQ_ITEM_DIAG_W,nr_seq_diag_p,NR_SEQ_ITEM_RESULT_W,clock_timestamp(),nm_usuario_p);

    ELSE
    
        DELETE FROM pe_prescr_item_result_diag WHERE 
            NR_SEQ_DIAG = nr_seq_diag_p AND
            NR_SEQ_ITEM = NR_SEQ_ITEM_RESULT_W;

        SELECT COUNT(1) INTO STRICT NR_COUNT_W FROM pe_prescr_item_result_diag a WHERE NR_SEQ_ITEM = NR_SEQ_ITEM_RESULT_W;

        if (NR_COUNT_W = 0) THEN
        
            DELETE FROM PE_PRESCR_ITEM_RESULT WHERE NR_SEQUENCIA = NR_SEQ_ITEM_RESULT_W;

        END IF;

    END IF;

    COMMIT;
END;
$body$
