CREATE OR REPLACE PROCEDURE atualiza_retorno_lote_ext_js () AS $body$
BEGIN

update 	lab_lote_externo a
set 	dt_retorno = clock_timestamp()
where 	not exists (	SELECT 1
			from prescr_procedimento b
			where b.ie_status_atend < 30
			and b.nr_seq_lote_externo = a.nr_sequencia )
and 	(a.dt_envio IS NOT NULL AND a.dt_envio::text <> '')
and 	coalesce(a.dt_retorno::text, '') = '';

commit;

end;
$body$
