CREATE OR REPLACE PROCEDURE atualiza_saldo_proj_rec_auto ( nr_seq_proj_rec_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
 
nr_sequencia_w		projeto_recurso_saldo.nr_sequencia%type;
			

BEGIN 
 
select	coalesce(max(nr_sequencia),0) 
into STRICT	nr_sequencia_w 
from	projeto_recurso_saldo 
where	nr_seq_projeto = nr_seq_proj_rec_p 
and	PKG_DATE_UTILS.START_OF(dt_mesano_referencia,'month') = PKG_DATE_UTILS.START_OF(clock_timestamp(),'month');
 
if (nr_sequencia_w = 0) then 
	CALL sup_gerar_saldo_projeto(nr_seq_proj_rec_p, trunc(clock_timestamp(),'dd'), cd_estabelecimento_p, nm_usuario_p);
else 
	CALL sup_atualizar_saldo_projeto(nr_sequencia_w, nr_seq_proj_rec_p, cd_estabelecimento_p, nm_usuario_p);
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
