SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_setor_atendimento ( nr_prescricao_p prescr_medica.nr_prescricao%type, nr_atendimento_p ATEND_PACIENTE_UNIDADE.NR_ATENDIMENTO%type) AS $body$
DECLARE


nr_seq_paciente_w		bigint;
nr_sequencia_CPOE_w		bigint;
cd_setor_atendimento_w	bigint;

C01 CURSOR FOR
SELECT nr_sequencia From CPOE_MATERIAL
where	nr_atendimento  = nr_atendimento_p;


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '' AND nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

    SELECT obter_unidade_atendimento(nr_atendimento_p,'A','CS')
    into STRICT  cd_setor_atendimento_w
;

    (if (cd_setor_atendimento_w) IS NOT NULL AND (if (cd_setor_atendimento_w))::text <> '') then
    
        update	prescr_medica
        set	cd_setor_atendimento = cd_setor_atendimento_w
        where	nr_prescricao = nr_prescricao_p;

        update	CAN_ORDEM_PROD
        set	cd_setor_paciente = cd_setor_atendimento_w
        where	nr_prescricao = nr_prescricao_p;

        update	PACIENTE_ATENDIMENTO
        set	cd_setor_atendimento = cd_setor_atendimento_w
        where	nr_prescricao = nr_prescricao_p;

        select protocolo.nr_seq_paciente 
        into STRICT nr_seq_paciente_w
        from paciente_setor protocolo
        inner join paciente_atendimento ciclo on ciclo.nr_seq_paciente = protocolo.nr_seq_paciente
        inner join prescr_medica prescricao on ciclo.nr_prescricao = prescricao.nr_prescricao
        where prescricao.nr_prescricao = nr_prescricao_p;

        update	PACIENTE_SETOR
        set	cd_setor_atendimento = cd_setor_atendimento_w
        where	nr_seq_paciente = nr_seq_paciente_w;

        open C01;
        loop
        fetch C01 into
            nr_sequencia_CPOE_w;
        EXIT WHEN NOT FOUND; /* apply on C01 */
            begin
                update	CPOE_MATERIAL
                set	cd_setor_atendimento = cd_setor_atendimento_w
                where	nr_sequencia  = nr_sequencia_CPOE_w;
            end;
        end loop;
        close C01;
    end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
