SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_setor_entrega_prescr ( nr_prescricao_p bigint) AS $body$
DECLARE


nr_sequencia_w		bigint;
cd_setor_atendimento_w	integer;

BEGIN

if (coalesce(nr_prescricao_p,0) > 0) then
	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_sequencia_w
	from	prescr_procedimento
	where	nr_prescricao = nr_prescricao_p
	and	(cd_setor_atendimento IS NOT NULL AND cd_setor_atendimento::text <> '');

	if (nr_sequencia_w > 0) then

		select	coalesce(max(cd_setor_atendimento),0)
		into STRICT	cd_setor_atendimento_w
		from	prescr_procedimento
		where	nr_prescricao = nr_prescricao_p
		and	nr_sequencia  = nr_sequencia_w;

		if (cd_setor_atendimento_w > 0) then

			update	prescr_medica
			set	cd_setor_entrega     = cd_setor_atendimento_w,
				cd_setor_atendimento = cd_setor_atendimento_w
			where	nr_prescricao = nr_prescricao_p;
		end if;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
