CREATE OR REPLACE PROCEDURE atualiza_setor_prescr_passagem ( nr_atendimento_p bigint, cd_setor_atendimento_p bigint) AS $body$
DECLARE


nr_prescricao_w		bigint;

c01 CURSOR FOR
SELECT	a.nr_prescricao
from	prescr_medica a
where	a.nr_atendimento	= nr_atendimento_p
and	a.dt_prescricao		> clock_timestamp() - interval '15 days'
and	coalesce(a.dt_baixa::text, '') = ''
and	exists (
		SELECT	1
		from	prescr_material b
		where	a.nr_prescricao	= b.nr_prescricao
		and	b.cd_motivo_baixa	= 0)
and	not exists (
		select	1
		from	cirurgia x
		where	x.nr_prescricao = a.nr_prescricao);


BEGIN

open c01;
loop
fetch c01 into
	nr_prescricao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	update	prescr_medica
	set	cd_setor_atendimento	= cd_setor_atendimento_p
	where	nr_prescricao		= nr_prescricao_w;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE atualiza_setor_prescr_passagem ( nr_atendimento_p bigint, cd_setor_atendimento_p bigint) FROM PUBLIC;

