CREATE OR REPLACE PROCEDURE atualiza_se_permite_visita ( nm_usuario_p text, nr_atendimento_p bigint, ie_permite_visita_p text) AS $body$
BEGIN
 
if (coalesce(ie_permite_visita_p,'S') = 'S') then 
	 
	update	atendimento_paciente 
	set	ie_permite_visita = 'N', 
		nm_usuario	 = nm_usuario_p 
	where	nr_atendimento = nr_atendimento_p;	
	 
elsif (ie_permite_visita_p = 'N') then 
	 
	update	atendimento_paciente 
	set	ie_permite_visita = 'S', 
		nm_usuario	 = nm_usuario_p 
	where	nr_atendimento = nr_atendimento_p;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
