CREATE OR REPLACE PROCEDURE atualiza_status_acha_perd ( nm_usuario_p text, ds_lista_p text, cd_estabelecimento_p bigint, nr_seq_tipo_baixa_p bigint, nr_seq_atend_achado_perd_p bigint, cd_pessoa_retirada_p text, nm_pessoa_retirada_p text, cd_cnpj_doacao_p text, nm_pessoa_juridica_doacao_p text, cd_pessoa_doacao_p text, nm_pessoa_doacao_p text, ds_obs_baixa_p text) AS $body$
DECLARE


ds_lista_w		varchar(1000);
tam_lista_w		bigint;
ie_pos_virgula_w	smallint;
nr_seq_item_p		bigint;
nr_seq_status_para_w	bigint;
qt_item_sem_baixa_w	bigint;


BEGIN

ds_lista_w := ds_lista_p;

while(ds_lista_w IS NOT NULL AND ds_lista_w::text <> '')  loop
	begin
	tam_lista_w		:= length(ds_lista_w);
	ie_pos_virgula_w	:= position(',' in ds_lista_w);

	if (ie_pos_virgula_w <> 0) then
		nr_seq_item_p := (substr(ds_lista_w,1,(ie_pos_virgula_w - 1)))::numeric;
		ds_lista_w	:= substr(ds_lista_w,(ie_pos_virgula_w + 1),tam_lista_w);
	end if;

	if (coalesce(cd_estabelecimento_p,0) > 0) then

		select	coalesce(max(nr_seq_status_baixa),0)
		into STRICT	nr_seq_status_para_w
		from	parametro_achado_perdido
		where	cd_estabelecimento = cd_estabelecimento_p;


			update	atend_achado_perdido_item
			set	dt_baixa                     = clock_timestamp(),
				nm_usuario    		     = nm_usuario_p,
				nr_seq_status_achado_perdido = CASE WHEN nr_seq_status_para_w=0 THEN null  ELSE nr_seq_status_para_w END ,
				nr_seq_tipo_baixa            = nr_seq_tipo_baixa_p,
				cd_pessoa_retirada           = substr(cd_pessoa_retirada_p,1,10),
				nm_pessoa_retirada           = substr(nm_pessoa_retirada_p,1,80),
				cd_cnpj_doacao               = substr(cd_cnpj_doacao_p,1,14),
				nm_pessoa_juridica_doacao    = substr(nm_pessoa_juridica_doacao_p,1,80),
				cd_pessoa_doacao             = substr(cd_pessoa_doacao_p,1,10),
				nm_pessoa_doacao             = substr(nm_pessoa_doacao_p,1,80),
				ds_obs_baixa		     = substr(ds_obs_baixa_p,1,255)
			where	nr_sequencia = nr_seq_item_p;

	end if;


	end;
	end loop;

select	coalesce(count(*),0)
into STRICT	qt_item_sem_baixa_w
from	atend_achado_perdido_item
where	nr_seq_atend_achado_perd = nr_seq_atend_achado_perd_p
and	coalesce(dt_baixa::text, '') = '';

if (qt_item_sem_baixa_w = 0) then

	update	atend_achado_perdido
	set	dt_baixa = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	nr_sequencia = nr_seq_atend_achado_perd_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
