CREATE OR REPLACE PROCEDURE atualiza_status_agend_futuro ( nr_cirurgia_p text, nm_usuario_p text) AS $body$
DECLARE


ie_status_agend_futuro_w	bigint;
nr_seq_atend_futuro_w		bigint;

BEGIN
ie_status_agend_futuro_w := obter_param_usuario(10000, 31, obter_perfil_ativo, obter_usuario_ativo, obter_estabelecimento_ativo, ie_status_agend_futuro_w);

if (nr_cirurgia_p IS NOT NULL AND nr_cirurgia_p::text <> '') and (coalesce(ie_status_agend_futuro_w,0) > 0) then


	select 	max(nr_seq_atend_futuro)
	into STRICT	nr_seq_atend_futuro_w
	from	agenda_paciente
	where	nr_cirurgia = nr_cirurgia_p;

	if (nr_seq_atend_futuro_w IS NOT NULL AND nr_seq_atend_futuro_w::text <> '') then
		update 	atend_pac_futuro
		set	nr_seq_status	= ie_status_agend_futuro_w,
			nm_usuario    	= nm_usuario_p
		where	nr_sequencia 	= nr_seq_atend_futuro_w;
	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
