CREATE OR REPLACE PROCEDURE atualiza_status_autor_agenda ( nr_seq_agenda_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_autorizacao_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_autorizacao_w
from	autorizacao_convenio
where	nr_seq_agenda = nr_seq_agenda_p;

if (qt_autorizacao_w = 0) then
	update	agenda_paciente
	set	ie_autorizacao 	= 'NN'
	where	nr_sequencia 	= nr_seq_agenda_p;
	commit;
end if;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
