SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_status_item_cme ( nr_sequencia_p bigint, nr_seq_item_p bigint, ie_status_item_p text, ie_unico_p text, ie_alteracao_p text ) AS $body$
DECLARE


ie_status_item_w	cm_conjunto_item.ie_status_item%type;
ie_unico_w		cm_item.ie_unico%type;


BEGIN

if (ie_alteracao_p = 'I') then

	if (ie_unico_p = 'S') then

		select 	max(ie_status_item)
		into STRICT	ie_status_item_w
		from	cm_conjunto_item
		where	nr_seq_item = nr_sequencia_p;

		if (coalesce(ie_status_item_w,'X') <> coalesce(ie_status_item_p,'X')) then

			update 	cm_conjunto_item
			set	ie_status_item = ie_status_item_p
			where	nr_seq_item = nr_sequencia_p;

		end if;

	end if;

	commit;

elsif (ie_alteracao_p = 'C') then

	select 	coalesce(ie_unico,'N'),
		ie_status_item
	into STRICT	ie_unico_w,
		ie_status_item_w
	from 	cm_item
	where	nr_sequencia = nr_seq_item_p;

	if (ie_unico_w = 'S' and (coalesce(ie_status_item_w,'X') <> coalesce(ie_status_item_p,'X'))) then

		update 	cm_item
		set	ie_status_item = ie_status_item_p
		where	nr_sequencia = nr_seq_item_p;

	end if;

	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
