CREATE OR REPLACE PROCEDURE atualiza_status_relat_geracao ( nr_sequencia_p bigint, ie_finalizou_p text, qt_paginas_p bigint, id_sessao_p text, ie_teste_automatizado_p text, nm_usuario_p text) AS $body$
DECLARE



ds_passo_geracao_w			varchar(4000);


BEGIN

if (ie_finalizou_p = 'S') then
	ds_passo_geracao_w := obter_desc_expressao(863612, 'Finalizado. Realizando download do relatório.');
else
	ds_passo_geracao_w := obter_desc_expressao(863610, 'Gerando página') || ' ' || qt_paginas_p;
end if;

update	log_relatorio_geracao
set		qt_paginas		= qt_paginas_p,
		ie_finalizado	= ie_finalizou_p,
		ds_passo_geracao 	= ds_passo_geracao_w,
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp(),
		id_sessao		= id_sessao_p,
		IE_TESTE_AUTOMATIZADO	= IE_TESTE_AUTOMATIZADO_p
where	nr_sequencia = nr_sequencia_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
