SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_status_transcricao ( nr_sequencia_p bigint, cd_novo_status_p text, nm_usuario_p text) AS $body$
DECLARE


ie_status_w	varchar(1);


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (cd_novo_status_p in ('S','T','E')) then
	select	max(ie_status)
	into STRICT	ie_status_w
	from	transcricao_prescricao
	where	nr_sequencia = nr_sequencia_p;

	if (ie_status_w = 'T') then
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(33812);
	else
	begin
	update	transcricao_prescricao
	set	ie_status 	= cd_novo_status_p,
		dt_atualizacao 	= clock_timestamp(),
		nm_usuario	= nm_usuario_p,
		dt_transcricao	= CASE WHEN cd_novo_status_p='S' THEN null  ELSE dt_transcricao END
	where	nr_sequencia	= nr_sequencia_p;
	end;
	end if;

	if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
