CREATE OR REPLACE PROCEDURE atualiza_sumarizado_cirurgia ( nr_cirurgia_p bigint, nr_seq_pepo_p bigint, nr_seq_modelo_p bigint, nm_usuario_p text, ie_sumarizado_p text) AS $body$
DECLARE


nr_sequencia_w w_flowsheet_config_grupo.nr_sequencia%type;


BEGIN

select  max(a.nr_sequencia)
into STRICT 	nr_sequencia_w
from  	w_flowsheet_config_grupo a,
		pepo_modelo_secao b
where  	coalesce(a.nr_cirurgia,0) = coalesce(nr_cirurgia_p,0)
and 	coalesce(a.nr_seq_pepo,0) = coalesce(nr_seq_pepo_p, 0)
and 	a.nm_usuario  		 = nm_usuario_p
and 	a.nr_seq_modelo 	 = nr_seq_modelo_p
and 	a.nr_seq_modelo_secao = b.nr_sequencia
and 	b.cd_secao		 = 'SV';


if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
	update 	w_flowsheet_config_grupo a
	set 	a.ie_sumarizado = ie_sumarizado_p
	where 	a.nr_sequencia = nr_sequencia_w;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
