CREATE OR REPLACE PROCEDURE atualiza_tab_preco_item_pend ( nr_sequencia_p bigint, nr_item_nf_p bigint, nm_usuario_p text) AS $body$
BEGIN

CALL Gerar_Preco_Venda(nr_sequencia_p, nr_item_nf_p, nm_usuario_p);

update	nota_fiscal_item
set	ie_pend_tab_preco	= 'N'
where	nr_sequencia		= nr_sequencia_p
and	nr_item_nf		= nr_item_nf_p;

CALL gerar_historico_nota_fiscal(
			nr_sequencia_p,
			nm_usuario_p,
			'52',
			wheb_mensagem_pck.get_texto(312533,'NR_ITEM_NF_P='||nr_item_nf_p));

commit;

end;
