SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_titulo_anterior ( ie_tipo_titulo_p text, cd_barras_final_p text, nr_seq_trans_fin_transf_p bigint, nr_titulo_p bigint) AS $body$
DECLARE


ie_tipo_titulo_w	varchar(2);


BEGIN

if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then

	if (coalesce(ie_tipo_titulo_p::text, '') = '') then
		select	ie_tipo_titulo
		into STRICT	ie_tipo_titulo_w
		from	titulo_pagar
		where 	nr_titulo    	= nr_titulo_p;
	else
		ie_tipo_titulo_w :=	ie_tipo_titulo_p;
	end if;

	update 	titulo_pagar
	set 	ie_tipo_titulo 	= ie_tipo_titulo_w,
		ie_situacao    	= 'T',
		nr_bloqueto    	= cd_barras_final_p,
		dt_liquidacao  	= clock_timestamp(),
		nr_seq_trans_fin_baixa = coalesce(nr_seq_trans_fin_baixa, nr_seq_trans_fin_transf_p)
	where 	nr_titulo    	= nr_titulo_p;

	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
