SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_titulo_escrit_barras ( nr_seq_escrit_p bigint, nr_titulo_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_banco_w			banco_estabelecimento.cd_banco%type;
qt_banco_w			bigint;
nr_bloqueto_w			titulo_pagar.nr_bloqueto%type;
ie_alterar_tipo_titulo_w	varchar(1);


BEGIN

select	max(a.nr_bloqueto)
into STRICT	nr_bloqueto_w
from	titulo_pagar a
where	a.nr_titulo	= nr_titulo_p;

if (nr_bloqueto_w IS NOT NULL AND nr_bloqueto_w::text <> '') then

	cd_banco_w	:= somente_numero(substr(nr_bloqueto_w,1,3));

	select	count(*)
	into STRICT	qt_banco_w
	from	banco a
	where	a.cd_banco	= cd_banco_w;

	if (coalesce(qt_banco_w,0)	> 0) then

		update	titulo_pagar_escrit
		set	ie_tipo_pagamento	= 'BLQ',
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()
		where	nr_titulo		= nr_titulo_p
		and	nr_seq_escrit		= nr_seq_escrit_p;
		

	end if;
	
	ie_alterar_tipo_titulo_w := Obter_Param_Usuario(857, 60, obter_perfil_ativo, nm_usuario_p, obter_estabelecimento_ativo, ie_alterar_tipo_titulo_w);
	
	if ( coalesce(ie_alterar_tipo_titulo_w,'N') = 'S' ) then
	
		update	titulo_pagar
		set	ie_tipo_titulo	= '1',
			ie_bloqueto	= 'S',
			nm_usuario	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
		where	nr_titulo	= nr_titulo_p;
	
	end if;

end if;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
