CREATE OR REPLACE PROCEDURE atualiza_tit_pagar_alt_valor ( nr_titulo_p bigint, vl_bordero_p bigint) AS $body$
DECLARE


qt_registros_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_registros_w
from	bordero_tit_pagar
where	nr_titulo	= nr_titulo_p;

if (qt_registros_w <> 0) then

	update	bordero_tit_pagar
	set	vl_bordero	= vl_bordero_p
	where	nr_titulo	= nr_titulo_p;

	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
