CREATE OR REPLACE PROCEDURE atualiza_usuario_lib_enf ( nm_usuario_p prescr_medica.nm_usuario_lib_enf%type, nr_prescricao_p prescr_medica.nr_prescricao%type) AS $body$
DECLARE


nm_usuario_lib_enf_w	prescr_medica.nm_usuario_lib_enf%type;
dt_liberacao_w			prescr_medica.dt_liberacao%type;


BEGIN
	select	max(a.nm_usuario_lib_enf),
			max(a.dt_liberacao)
	into STRICT	nm_usuario_lib_enf_w,
			dt_liberacao_w
	from	prescr_medica a
	where	a.nr_prescricao = nr_prescricao_p;

	if ((nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and
		coalesce(nr_prescricao_p, 0) > 0 and
		coalesce(dt_liberacao_w::text, '') = '') then

		update	prescr_medica
		set		nm_usuario_lib_enf = nm_usuario_p
		where	nr_prescricao = nr_prescricao_p;

	elsif (coalesce(nm_usuario_p::text, '') = '' and
			coalesce(nr_prescricao_p, 0) > 0 and
			coalesce(dt_liberacao_w::text, '') = '') then

		update	prescr_medica
		set		nm_usuario_lib_enf  = NULL
		where	nr_prescricao = nr_prescricao_p;

	elsif (nm_usuario_lib_enf_w IS NOT NULL AND nm_usuario_lib_enf_w::text <> '' AND dt_liberacao_w IS NOT NULL AND dt_liberacao_w::text <> '') then
		
		-- Prescricao ja liberada
		CALL wheb_mensagem_pck.exibir_mensagem_abort(1154645);
	end if;

	if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then
		commit;
	end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
