CREATE OR REPLACE PROCEDURE atualiza_valores_titulo_pagar ( vl_out_desp_bordero_p bigint, vl_juros_bordero_p bigint, vl_multa_bordero_p bigint, vl_desconto_bordero_p bigint, vl_bordero_p bigint, nr_titulo_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (coalesce(nr_titulo_p, 0) > 0) then
	begin
	update	titulo_pagar
	set	vl_out_desp_bordero	= vl_out_desp_bordero_p,
		vl_juros_bordero		= vl_juros_bordero_p,
		vl_multa_bordero		= vl_multa_bordero_p,
		vl_desconto_bordero	= vl_desconto_bordero_p,
		vl_bordero = vl_bordero_p,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario     		= nm_usuario_p
	where	nr_titulo			= nr_titulo_p;
	commit;
	end;
end if;
end;
$body$
