CREATE OR REPLACE PROCEDURE atualiza_valor_dep_ident ( nr_seq_deposito_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ie_commit_p text) AS $body$
DECLARE


vl_total_w	double precision;
vl_titulo_w	double precision;
vl_cheque_w	double precision;
cd_moeda_w	deposito_identificado.cd_moeda%type;


BEGIN

select	coalesce(max(cd_moeda),obter_moeda_padrao_empresa(cd_estabelecimento_p,'E'))
into STRICT 	cd_moeda_w
from	deposito_identificado
where	nr_Sequencia = nr_seq_deposito_p;

if (nr_seq_deposito_p IS NOT NULL AND nr_seq_deposito_p::text <> '') then
	select	sum(coalesce(a.vl_depositar,0))
	into STRICT	vl_titulo_w
	from	deposito_ident_titulo a
	where	a.nr_seq_deposito	= nr_seq_deposito_p;

	select	coalesce(sum(a.vl_depositar),0)
	into STRICT	vl_cheque_w
	from	deposito_ident_cheque a
	where	a.nr_seq_deposito	= nr_seq_deposito_p;

	vl_total_w:= coalesce(vl_titulo_w,0) + coalesce(vl_cheque_w,0);

	update	deposito_identificado
	set	vl_deposito		= vl_total_w,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia	= nr_seq_deposito_p;

	if (cd_moeda_w <> obter_moeda_padrao_empresa(cd_estabelecimento_p,'E')) then
		update	deposito_identificado
		set	vl_deposito_estrang	= (coalesce(vl_total_w,0) / CASE WHEN coalesce(vl_cotacao,0)=0 THEN 1  ELSE vl_cotacao END )
		where	nr_sequencia		= nr_seq_deposito_p;

	else
		update	deposito_identificado
		set	vl_deposito_estrang	 = NULL
		where	nr_sequencia		= nr_seq_deposito_p;
	end if;
end if;

if (coalesce(ie_commit_p,'S') = 'S') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
