CREATE OR REPLACE PROCEDURE atualiza_valor_gps ( nr_seq_gps_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
vl_gps_w	double precision;

 

BEGIN 
 
select	coalesce(sum((obter_dados_tit_pagar(nr_titulo,'V'))::numeric ),0) 
into STRICT	vl_gps_w 
from	gps_titulo 
where	nr_seq_gps = nr_seq_gps_p;
 
 
update	gps 
set   vl_gps 		= vl_gps_w, 
	vl_inss		= vl_gps_w 
where	nr_sequencia	= nr_seq_gps_p;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
