CREATE OR REPLACE PROCEDURE atualiza_valor_partic_conta ( nr_cirurgia_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
										 
nr_sequencia_w	bigint;
cd_convenio_w	integer;										
										 
C01 CURSOR FOR 
SELECT	b.nr_Sequencia, 
		b.cd_convenio 
from	Atendimento_paciente c, 
		conta_paciente x, 
		procedimento_paciente b, 
		cirurgia a 
where	a.nr_cirurgia		= nr_cirurgia_p 
and		a.nr_atendimento 	= b.nr_atendimento 
and		a.nr_atendimento 	= c.nr_atendimento 
and		a.nr_cirurgia		= b.nr_cirurgia 
and		b.nr_interno_conta	= x.nr_interno_conta 
and		x.ie_status_acerto	= 1;
													
										 

BEGIN 
 
	 
open C01;
loop 
fetch C01 into	 
	nr_sequencia_w, 
	cd_convenio_W;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	CALL atualiza_preco_procedimento(nr_Sequencia_w, cd_convenio_w, nm_usuario_p);
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
