SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_valor_reg_lic_item ( nr_sequencia_p bigint, vl_item_p bigint, ie_qualif_lance_fornec_p text) AS $body$
DECLARE


nr_seq_licitacao_w			bigint;
nr_seq_lic_item_w			bigint;
nr_seq_lance_anterior_w		bigint;
nr_seq_fornec_w			bigint;
vl_item_w				double precision;


BEGIN

if (vl_item_p > 0) then
	begin
	update	reg_lic_item_fornec
	set	vl_item			= vl_item_p,
		ie_qualif_lance_fornec	= ie_qualif_lance_fornec_p
	where	nr_sequencia		= nr_sequencia_p
	and	vl_item_p > 0;
	end;
else

	select	nr_seq_licitacao,
		nr_seq_lic_item,
		nr_seq_fornec
	into STRICT	nr_seq_licitacao_w,
		nr_seq_lic_item_w,
		nr_seq_fornec_w
	from	reg_lic_item_fornec
	where	nr_sequencia	= nr_sequencia_p;

	/*Busca o maior lance ativo do fornecedor para o item*/

	select	coalesce(max(nr_seq_lance), 0) -1
	into STRICT	nr_seq_lance_anterior_w
	from	reg_lic_fornec_lance
	where	nr_seq_licitacao		= nr_seq_licitacao_w
	and	nr_seq_lic_item		= nr_seq_lic_item_w
	and	nr_seq_fornec		= nr_seq_fornec_w;
	--and	nvl(ie_qualificado,'S')	= 'S';
	if (nr_seq_lance_anterior_w > 0) then
		/*Busca o valor do lance anterior referente aquele item e aquele fornecedor*/

		select	coalesce(min(vl_item), 0)
		into STRICT	vl_item_w
		from	reg_lic_fornec_lance
		where	nr_seq_licitacao	= nr_seq_licitacao_w
		and	nr_seq_lic_item	= nr_seq_lic_item_w
		and	nr_seq_fornec	= nr_seq_fornec_w
		and	nr_seq_lance	= nr_seq_lance_anterior_w;

		update	reg_lic_item_fornec
		set	vl_item			= vl_item_w,
			ie_qualif_lance_fornec	= ie_qualif_lance_fornec_p
		where	nr_sequencia		= nr_sequencia_p;
	else
		/*Caso n√£o tenha nenhum lance dai busca a proposta inicial*/

		update	reg_lic_item_fornec
		set	vl_item			= vl_original,
			ie_qualif_lance_fornec	= ie_qualif_lance_fornec_p
		where	nr_sequencia		= nr_sequencia_p;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
