CREATE OR REPLACE PROCEDURE atualiza_versao_tasy ( cd_aplicacao_p text, cd_versao_p text, nm_usuario_p text, IE_VERSAO_OFICIAL_P text default 'N') AS $body$
DECLARE


qt_tab_exportar_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_tab_exportar_w
from	tasy_exptables
where coalesce(ie_nao_exportar,'S') = 'N';

insert into aplicacao_tasy_versao(
		cd_aplicacao_tasy,
		cd_versao,
		dt_versao,
		dt_atualizacao,
		nm_usuario,
		ds_observacao,
		ie_versao_oficial,
		qt_tab_exportada)
Values (
		cd_aplicacao_p,
		cd_versao_p,
		clock_timestamp(),
		clock_timestamp(),
		nm_usuario_p,
		'',
		IE_VERSAO_OFICIAL_P,
		qt_tab_exportar_w);

CALL exec_sql_dinamico('ATUALIZA_VERSAO_TASY','update		tasy_versao.tasy_seg
		set 	ie_atualizando_versao = ''N'' where	upper(nm_user) = user');

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
