CREATE OR REPLACE PROCEDURE atualiza_via_forma_farm ( cd_material_p bigint, nr_seq_forma_farm_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_via_aplicacao_w		varchar(5);

c01 CURSOR FOR
	SELECT	a.ie_via_aplicacao
	from	anvisa_forma_farm_via b,
		via_aplicacao a
	where	a.nr_seq_via_anvisa	= b.nr_seq_via_adm
	and	b.nr_seq_forma_farm	= nr_Seq_forma_farm_p;


BEGIN

delete	FROM mat_via_aplic
where	cd_material	= cd_material_p;

open c01;
	loop
	fetch c01 into
		ie_via_aplicacao_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		insert into mat_via_aplic(
			nr_sequencia,
			cd_material,
			dt_atualizacao,
			nm_usuario,
			ie_via_aplicacao,
			dt_atualizacao_nrec,
			nm_usuario_nrec)
		values (nextval('mat_via_aplic_seq'),
			cd_material_p,
			clock_timestamp(),
			nm_usuario_p,
			ie_via_aplicacao_w,
			clock_timestamp(),
			nm_usuario_p);

		end;
	end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
